import React, { useContext, useState } from 'react';
import { AuthContext } from '../pages/AuthContext';

const UserProfile = () => {
  const { user, logout, updateProfile } = useContext(AuthContext);
  const [form, setForm] = useState({ age: user.age, sex: user.sex });
  const [profilePic, setProfilePic] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setProfilePic(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateProfile(form.age, form.sex, profilePic);
  };

  return (
    <div>
      <h1>{user.username}'s Profile</h1>
      <button onClick={logout}>Logout</button>
      <form onSubmit={handleSubmit}>
        <input type="number" name="age" value={form.age} onChange={handleChange} placeholder="Age" />
        <input type="text" name="sex" value={form.sex} onChange={handleChange} placeholder="Sex" />
        <input type="file" name="profilePic" onChange={handleFileChange} />
        <button type="submit">Update Profile</button>
      </form>
      {user.profilePic && <img src={`/${user.profilePic}`} alt="Profile" />}
      <h2>Saved Recipes</h2>
      <ul>
        {user.savedRecipes.map(recipe => (
          <li key={recipe._id}>{recipe.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default UserProfile;
