import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Recipes = () => {
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    axios.get('/api/recipes')
      .then(response => {
        if (response.data) {
          setRecipes(response.data);
        } else {
          throw new Error('Invalid JSON response');
        }
      })
      .catch(error => {
        console.error('Error fetching recipes:', error);
        setError('Error fetching recipes');
      });
  }, []);

  const handleCreate = async (recipe) => {
    try {
      const response = await axios.post('/api/recipes', recipe);
      console.log('Response from server:', response.data); // Log the server response
      setRecipes([...recipes, response.data]);
    } catch (error) {
      console.error('Error creating recipe:', error); // Log the error
    }
  };

  const handleUpdate = async (id, updatedRecipe) => {
    try {
      const response = await axios.put(`/api/recipes/${id}`, updatedRecipe);
      if (response.data) {
        setRecipes(recipes.map(recipe => (recipe._id === id ? response.data : recipe)));
      } else {
        throw new Error('Invalid JSON response');
      }
    } catch (error) {
      console.error('Error updating recipe:', error);
      setError('Error updating recipe');
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/recipes/${id}`);
      setRecipes(recipes.filter(recipe => recipe._id !== id));
    } catch (error) {
      console.error('Error deleting recipe:', error);
      setError('Error deleting recipe');
    }
  };

  return (
    <div>
      <h1>Recipes</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error */}
      <ul>
        {recipes.map(recipe => (
          <li key={recipe._id}>
            <h2>{recipe.title}</h2>
            <p>{recipe.instructions}</p>
            <ul>
              {recipe.ingredients.map((ingredient, index) => (
                <li key={index}>{ingredient}</li>
              ))}
            </ul>
            <button onClick={() => handleUpdate(recipe._id, { title: 'New Title' })}>Update</button>
            <button onClick={() => handleDelete(recipe._id)}>Delete</button>
          </li>
        ))}
      </ul>
      <button onClick={() => handleCreate({ title: 'New Recipe', ingredients: ['Ingredient 1'], instructions: 'Instructions' })}>
        Add Recipe
      </button>
    </div>
  );
};

export default Recipes;
