import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import '../styles/header.css';
import logo from '../images/logo.png';
import SignInSignUpModal from './pages/authModal';  // Import the modal
import { AuthContext } from '../pages/AuthContext';

function Header() {
  const { user, logout } = useContext(AuthContext);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState('signIn');  // Default to 'signIn'

  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <header className="header">
      <div className="header__logo">
        <img src={logo} alt="Logo" />
      </div>

      <nav className="header__nav">
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/recipes">Recipes</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
          <li>
            <Link to="/contact">Contact</Link>
          </li>
        </ul>
      </nav>

      <div className="header__auth">
        {user ? (
          <>
            <Link to="/profile" className="auth-link">Profile</Link>
            <span> | </span>
            <button onClick={logout} className="auth-link">Logout</button>
          </>
        ) : (
          <>
            <button onClick={() => openModal('signIn')} className="auth-link">Sign In</button>
            <span> | </span>
            <button onClick={() => openModal('signUp')} className="auth-link">Sign Up</button>
          </>
        )}
      </div>

      <SignInSignUpModal isOpen={isModalOpen} onClose={closeModal} initialFormType={modalType} />
    </header>
  );
}

export default Header;
