import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css'; // Import your header styles

const Header = () => {
  const [showSignIn, setShowSignIn] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);
  const [user, setUser] = useState(null); // Assuming user state to manage login status

  const handleSignInClick = () => setShowSignIn(!showSignIn);
  const handleSignUpClick = () => setShowSignUp(!showSignUp);
  const handleLogout = () => {
    setUser(null); // Perform logout logic here
  };

  return (
    <header className="header">
      <div className="logo">
        <Link to="/">Recipe App</Link>
      </div>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/about">About</Link></li>
          <li><Link to="/recipes">Recipes</Link></li>
          <li><Link to="/contact">Contact</Link></li>
        </ul>
      </nav>
      <div className="auth-section">
        {user ? (
          <div className="profile-section">
            <span>{user.username}</span>
            <button onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <div>
            <button onClick={handleSignInClick}>Sign In</button>
            <button onClick={handleSignUpClick}>Sign Up</button>
          </div>
        )}
      </div>
      {showSignIn && <SignInForm />}
      {showSignUp && <SignUpForm />}
    </header>
  );
};

const SignInForm = () => {
  return (
    <div className="form-container">
      <h2>Sign In</h2>
      {/* Add your Sign In form here */}
    </div>
  );
};

const SignUpForm = () => {
  return (
    <div className="form-container">
      <h2>Sign Up</h2>
      {/* Add your Sign Up form here */}
    </div>
  );
};

export default Header;
