import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../pages/AuthContext';
import '../styles/userProfile.css';

const UserProfile = () => {
  const { user, updateProfile } = useContext(AuthContext);
  const [formData, setFormData] = useState({ age: '', sex: '', profilePic: null });
  const [preview, setPreview] = useState('');

  useEffect(() => {
    if (user) {
      setFormData({ age: user.age || '', sex: user.sex || '', profilePic: null });
      setPreview(user.profilePic || '');
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'profilePic') {
      setFormData({ ...formData, [name]: files[0] });
      setPreview(URL.createObjectURL(files[0]));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { age, sex, profilePic } = formData;
    await updateProfile(age, sex, profilePic);
  };

  return (
    <div className="user-profile">
      <h1>User Profile</h1>
      {user && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Age</label>
            <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Sex</label>
            <input
              type="text"
              name="sex"
              value={formData.sex}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Profile Picture</label>
            <input
              type="file"
              name="profilePic"
              onChange={handleChange}
            />
            {preview && <img src={preview} alt="Profile Preview" className="profile-pic-preview" />}
          </div>
          <button type="submit">Update Profile</button>
        </form>
      )}
    </div>
  );
};

export default UserProfile;
