import React, { useState, useContext } from 'react';
import { AuthContext } from '../pages/AuthContext';
import '../styles/authModal.css';

const SignInSignUpModal = ({ isOpen, onClose, initialFormType }) => {
  const { login, register } = useContext(AuthContext);
  const [isSignIn, setIsSignIn] = useState(initialFormType === 'signIn');
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    age: '',
    sex: ''
  });

  if (!isOpen) {
    return null;
  }

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isSignIn) {
        await login(formData.username, formData.password);
        onClose();
        window.location.reload(); // Optional: Reload to update the UI
      } else {
        if (formData.password !== formData.confirmPassword) {
          alert('Passwords do not match');
          return;
        }
        await register(formData.username, formData.password, formData.age, formData.sex);
        onClose();
        window.location.reload(); // Optional: Reload to update the UI
      }
    } catch (err) {
      alert(err.message);
    }
  };


  return (
    <div className="auth-modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>{isSignIn ? 'Sign In' : 'Sign Up'}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Username</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          {!isSignIn && (
            <>
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Age</label>
                <input
                  type="number"
                  name="age"
                  value={formData.age}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Sex</label>
                <input
                  type="text"
                  name="sex"
                  value={formData.sex}
                  onChange={handleChange}
                  required
                />
              </div>
            </>
          )}
          <button type="submit">{isSignIn ? 'Sign In' : 'Sign Up'}</button>
        </form>
        <button onClick={() => setIsSignIn(!isSignIn)}>
          {isSignIn ? 'Create an Account' : 'Already have an Account? Sign In'}
        </button>
      </div>
    </div>
  );
};

export default SignInSignUpModal;
