import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../pages/AuthContext';
import '../styles/userProfile.css'; // Import your CSS for UserProfile

const UserProfile = () => {
  const { user, updateProfile, logout } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    age: '',
    sex: '',
    profilePic: null
  });

  useEffect(() => {
    if (user) {
      setFormData({
        age: user.age || '',
        sex: user.sex || '',
        profilePic: null
      });
    }
  }, [user]);

  const handleChange = (e) => {
    if (e.target.name === 'profilePic') {
      setFormData({ ...formData, [e.target.name]: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await updateProfile(formData.age, formData.sex, formData.profilePic);
  };

  return (
    <div className="user-profile">
      <h1>User Profile</h1>
      <div className="profile-info">
        <img src={user.profilePic || '/default-profile-pic.png'} alt="Profile" className="profile-pic" />
        <div className="profile-details">
          <p>Username: {user.username}</p>
          <p>Age: {user.age}</p>
          <p>Sex: {user.sex}</p>
        </div>
      </div>
      <form onSubmit={handleSubmit} className="profile-form">
        <div className="form-group">
          <label>Age</label>
          <input
            type="number"
            name="age"
            placeholder="Update Age"
            value={formData.age}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Sex</label>
          <input
            type="text"
            name="sex"
            placeholder="Update Sex"
            value={formData.sex}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Profile Picture</label>
          <input
            type="file"
            name="profilePic"
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="submit-button">Update Profile</button>
      </form>
      <button onClick={logout} className="logout-button">Logout</button>
    </div>
  );
};

export default UserProfile;
