import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../pages/AuthContext';
import SignInSignUpModal from '../pages/authModal'; // Import AuthModal component

const Header = () => {
  const { user, login, register, logout } = useContext(AuthContext);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [registerForm, setRegisterForm] = useState({ username: '', password: '', age: '', sex: '' });
  const [isModalOpen, setIsModalOpen] = useState(false); // Manage modal visibility
  const [initialFormType, setInitialFormType] = useState('signIn'); // Initial form type

  const handleLoginChange = (e) => {
    setLoginForm({ ...loginForm, [e.target.name]: e.target.value });
  };

  const handleRegisterChange = (e) => {
    setRegisterForm({ ...registerForm, [e.target.name]: e.target.value });
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    login(loginForm.username, loginForm.password);
    setIsModalOpen(false); // Close the modal on successful login
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    register(registerForm.username, registerForm.password, registerForm.age, registerForm.sex);
    setIsModalOpen(false); // Close the modal on successful registration
  };

  const openSignInModal = () => {
    setInitialFormType('signIn');
    setIsModalOpen(true);
  };

  const openSignUpModal = () => {
    setInitialFormType('signUp');
    setIsModalOpen(true);
  };

  return (
    <header className="header">
      <nav className="header__nav">
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/about">About</Link></li>
          <li><Link to="/recipes">Recipes</Link></li>
          <li><Link to="/contact">Contact</Link></li>
          {user ? (
            <>
              <li><Link to="/profile">Profile</Link></li>
              <li><button onClick={logout}>Logout</button></li>
            </>
          ) : (
            <>
              <li><button onClick={openSignInModal}>Sign In</button></li>
              <li><button onClick={openSignUpModal}>Sign Up</button></li>
            </>
          )}
        </ul>
      </nav>
      {/* Render the AuthModal component */}
      <SignInSignUpModal 
        isOpen={isModalOpen} 
        onClose={() => setIsModalOpen(false)} 
        initialFormType={initialFormType} 
      />
    </header>
  );
};

export default Header;
