import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['x-auth-token'] = token;
      axios.get('/api/auth/profile').then(res => {
        setUser(res.data);
        setLoading(false);
      }).catch(err => {
        localStorage.removeItem('token');
        setLoading(false);
      });
    } else {
      setLoading(false);
    }
  }, []);

  const login = async (username, password) => {
    const res = await axios.post('/api/auth/login', { username, password });
    localStorage.setItem('token', res.data.token);
    axios.defaults.headers.common['x-auth-token'] = res.data.token;
    setUser(res.data.user);
  };

  const register = async (username, password, age, sex) => {
    const res = await axios.post('/api/auth/register', { username, password, age, sex });
    localStorage.setItem('token', res.data.token);
    axios.defaults.headers.common['x-auth-token'] = res.data.token;
    setUser(res.data.user);
  };

  const updateProfile = async (age, sex, profilePic) => {
    const formData = new FormData();
    if (age) formData.append('age', age);
    if (sex) formData.append('sex', sex);
    if (profilePic) formData.append('profilePic', profilePic);

    await axios.post('/api/auth/profile', formData);
    const res = await axios.get('/api/auth/profile');
    setUser(res.data);
  };


  const logout = () => {
    localStorage.removeItem('token');
    axios.defaults.headers.common['x-auth-token'] = '';
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, register, updateProfile, logout, loading }}>
      {loading ? <p>Loading...</p> : children}
    </AuthContext.Provider>
  );
};
