import React, { useEffect, useState } from 'react';
import './recipes.css'; // Import the CSS file

const Recipes = ({ Data }) => {
  const [recipes, setRecipes] = useState(Data || []);  // Initialize with Data if available
  const [search, setSearch] = useState('');
  const [filteredRecipes, setFilteredRecipes] = useState(Data || []);  // Initialize with Data if available

  useEffect(() => {
    if (!Data) {
      fetch('http://localhost:3001/api/recipes')  // Fetch from the correct URL if Data prop is not available
        .then((response) => response.json())
        .then((data) => {
          setRecipes(data);
          setFilteredRecipes(data);  // Initialize with all recipes
        })
        .catch((error) => console.error('Error fetching recipes:', error));
    }
  }, [Data]);  // Only fetch data if Data is not provided

  useEffect(() => {
    // Filter recipes based on the search term
    setFilteredRecipes(
      recipes.filter(recipe =>
        recipe.title.toLowerCase().includes(search.toLowerCase()) ||
        recipe.ingredients.some(ingredient =>
          ingredient.toLowerCase().includes(search.toLowerCase())
        )
      )
    );
  }, [search, recipes]);

  return (
    <div className="recipes-container">
      <h1>Recipes</h1>
      <input
        type="text"
        placeholder="Search recipes..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <div className="recipes">
        {filteredRecipes.map((recipe) => (
          <div className="recipe" key={recipe._id}>
            <h2>{recipe.title}</h2>
            <ul>
              {recipe.ingredients.map((ingredient, index) => (
                <li key={index}>{ingredient}</li>
              ))}
            </ul>
            <p>{recipe.instructions}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recipes;
