//Display and allow updating of user profile information, including age, sex, and profile picture.
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/userProfile.css';

const UserProfile = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    age: '',
    sex: '',
    profilePic: ''
  });

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/users/${userId}`);
        setUser(response.data);
        setFormData({
          username: response.data.username,
          password: '',
          age: response.data.age || '',
          sex: response.data.sex || '',
          profilePic: response.data.profilePic || ''
        });
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };
    fetchUserProfile();
  }, [userId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3001/api/users/${userId}`, formData);
      setUser({ ...user, ...formData });
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating user profile:', error);
    }
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div className="user-profile">
      <div className="profile-header">
        <img src={user.profilePic || 'default-profile.png'} alt="Profile" />
        <h2>{user.username}</h2>
      </div>
      <div className="profile-info">
        {isEditing ? (
          <form onSubmit={handleSubmit}>
            <input type="text" name="username" value={formData.username} onChange={handleChange} placeholder="Username" />
            <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Password" />
            <input type="number" name="age" value={formData.age} onChange={handleChange} placeholder="Age" />
            <input type="text" name="sex" value={formData.sex} onChange={handleChange} placeholder="Sex" />
            <input type="text" name="profilePic" value={formData.profilePic} onChange={handleChange} placeholder="Profile Picture URL" />
            <button type="submit">Update Profile</button>
          </form>
        ) : (
          <>
            <p>Age: {user.age}</p>
            <p>Sex: {user.sex}</p>
            <p>Profile Picture: {user.profilePic}</p>
            <button onClick={() => setIsEditing(true)}>Edit Profile</button>
          </>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
