//Main application component, sets up routes and provides the authentication context to the application.
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import About from './pages/About';
import Recipes from './pages/Recipes';
import Contact from './pages/Contact';
import Home from './pages/Home';
import UserProfile from './components/UserProfile';
import { AuthProvider } from './pages/AuthContext';  // Ensure the correct path
import SignInSignUpModal from './pages/authModal'; // Import AuthModal component
import ProfilePage from '../pages/Profile';  // Import ProfilePage
import NotFound from '../pages/NotFound';

function App() {
  const [backendData, setBackendData] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // Manage modal visibility
  const [initialFormType, setInitialFormType] = useState('signIn'); // Initial form type

  useEffect(() => {
    fetch('http://localhost:3001/api/recipes')
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched data in App:', data); // Log data for debugging
        setBackendData(data);
      })
      .catch((error) => {
        console.error('Error fetching recipes:', error);
      });
  }, []);

  return (
    <Router>
      <AuthProvider>
        <Header 
          openSignInModal={() => {
            setInitialFormType('signIn');
            setIsModalOpen(true);
          }}
          openSignUpModal={() => {
            setInitialFormType('signUp');
            setIsModalOpen(true);
          }}
        />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/recipes" element={<Recipes Data={backendData} />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/profile" element={<UserProfile />} />
        </Routes>
        <SignInSignUpModal 
          isOpen={isModalOpen} 
          onClose={() => setIsModalOpen(false)} 
          initialFormType={initialFormType} 
        />
      </AuthProvider>
    </Router>
  );
}

export default App;
